/**
 * This JS queries the /notification route and displays notification according to the
 * received JSON.
 *
 * TODO: Could we simply send the values to a TWIG template? It feels like building the
 * HTML is not the most optimal way to do it.
 *
 * TODO: This JavaScript is NOT tested. Test it!
 */
(function ($, Drupal) {
    'use strict';

    var notificationContainer;
    var cookieExpirationInHours = 48;
    var secondsInAnHour = 3600000;
    var localStorageId = 'notifications';
    var containerId = '#cogeco-notifications';

    removeExpiredNotificationsFromLocalStorage();
    initialize();

    function initialize() {
        var ajaxSettings = {
            url: "/" + getCurrentLanguage() + "/notifications?language=" + getLanguageCode() + '&currentUrl=' + currentUrlWithoutLanguage(window.location.pathname)
        };
        if(getCall()) {
          $.ajax(ajaxSettings).done(buildNotificationsMarkup);
        }

    }

    /**
     * @param notifications
     */
    function buildNotificationsMarkup(notifications) {
        var notificationsToDisplay = notifications.filter(notificationShouldDisplay);

        if (notificationsToDisplay.length > 0) {
            notificationContainer = $(containerId);
            notificationContainer.append('<ul></ul>');

            var notificationsContainer = notificationContainer.find('ul');

            notificationsToDisplay.forEach(function (notification) {
                notificationsContainer.append(buildNotificationMarkup(notification));
            });

            assignCloseButtonClickHandlers();
            assignNotificationClickHandlers();
            setupDotdotdot();
        }
    }

    /**
     * @param notification
     * @returns {string}
     */
    function buildNotificationMarkup(notification) {
        var persistentClass = (notification.persistent == "1") ? "persistent " : "";
        var notificationMarkup = "<li class='" + persistentClass + "theme-" + notification.type + "' data-id='" + notification.id + "'><div class='container'><div class='row'><div class='col-12 ticker-style'><div class='information'>";
        notificationMarkup += notification.body;
        notificationMarkup += "</div><span class='close'></span></div></div></div></li>";

        return notificationMarkup;
    }

    function assignCloseButtonClickHandlers() {
        notificationContainer.find('li .close').one('click', function () {
            var currentNotification = $(this).parents('li');
            var data = {
                'id': currentNotification.data('id'),
                'expiration': getCurrentTime() + (cookieExpirationInHours * secondsInAnHour)
            };

            appendToLocalStorage(data);
            currentNotification.addClass('inactive');
        });
    }

    function assignNotificationClickHandlers() {
        notificationContainer.find('li .information').one('click', function () {
            // Destroy the dotdotdot watch handler
            $(this).trigger('destroy');
            $(this).parents('li').addClass('expand');
        });
    }

    function setupDotdotdot() {
        notificationContainer.find('li .information').dotdotdot({
            watch: 'window'
        });
    }

    /**
     * @param notification
     * @returns {boolean}
     */
    function notificationShouldDisplay(notification) {
        var shouldDisplay = true;

        var closedNotifications = getNotificationStorage();
        closedNotifications.forEach(function (closedNotification) {
            if (closedNotification.id === parseInt(notification.id) && shouldDisplay) {
                shouldDisplay = false;
            }
        });

        return shouldDisplay;
    }

    function removeExpiredNotificationsFromLocalStorage() {
        var notificationStorage = getNotificationStorage();
        var currentTime = getCurrentTime();

        notificationStorage.forEach(function (notification, index) {
            if (notificationStorage[0].expiration <= currentTime) {
                notificationStorage.splice(index, 1);
            }
        });

        localStorage.removeItem(localStorageId);
        localStorage.setItem(localStorageId, JSON.stringify(notificationStorage));
    }

    /**
     * @returns {Array}
     */
    function getNotificationStorage() {
        var notificationStorage = JSON.parse(localStorage.getItem(localStorageId));
        return notificationStorage === null ? [] : notificationStorage;
    }

    /**
     * @param data
     */
    function appendToLocalStorage(data) {
        var notificationStorage = getNotificationStorage();
        notificationStorage.push(data);
        localStorage.setItem(localStorageId, JSON.stringify(notificationStorage));
    }

    /**
     * @returns {number}
     */
    function getCurrentTime() {
        return new Date().getTime();
    }

    /**
     * @param path
     * @returns {string}
     */
    function currentUrlWithoutLanguage(path) {
        var cleanedPath = path.replace('/en', '').replace('/fr', '');
        return cleanedPath === '' ? '/' : cleanedPath;
    }

  /**
   *
   * @returns {string}
   */
    function getCurrentLanguage() {
        return window.location.pathname.match(/^\/fr.*/) ? 'fr' : 'en';
    }

    /**
     *
     * @returns {string}
     */
    function getLanguageCode() {
        var body = $("body");
        return getCurrentLanguage() + "-" + body.data("province") + "-" + body.data("zone");
    }

    /**
     *
     * @returns {boolean}
     */
    function getCall() {
      let notifications = getNotificationStorage();
      if(notifications.length > 0) {
        if(notifications[0]['expiration'] <= getCurrentTime()){
         return false;
        }
      } else {
        return true;
      }
    }

})(jQuery, Drupal);
