(function($, _, drupalSettings){
  $(function(){

    var currentLanguage = "en";
    if ($('body').hasClass('lang-fr')) {
      currentLanguage = "fr";
    }

    // Get Social url params
    getParamsCampaign(window.location, currentLanguage);

    // Get social params
    function getParamsCampaign(url, currentLanguage) {
      let params = [
        'gclid',
        'dclid',
        'cx',
        'ie',
        'cof',
        'siteurl',
        'fclsrc',
        'zanpid',
        'os_ehash',
        'origin',
        'hConversionEventId',
        'utm_source',
        'utm_campaign',
        'utm_content',
        'utm_term',
        'fbclid'
      ];
      let trakingItems = {};
      let page_url = url.search.substring(1).replace(/&amp;/g, '&');
      let parameters = page_url.split('&');
      let found = false;
      for (let i = 0; i < parameters.length; i++) {
        let url_param = parameters[i].split('=');
        for (let key in params) {
          if(url_param[0] == params[key]) {
            found = true;
            trakingItems[params[key]] = url_param[1];
          }
        }
      }
      if (found) {
        let data = {
          'page': {
            'url': url.href,
            'pathname': url.pathname
          }
        };
        for (let param in params) {
          if (!_.isEmpty(trakingItems[params[param]])) {
            data.page[params[param]] = trakingItems[params[param]];
          }
        }
        sessionStorage.setItem('params', JSON.stringify(data));
        window.dataLayer = window.dataLayer || [];
        dataLayer.push(data);
      }

      let session = JSON.parse(sessionStorage.getItem('params'));
      if (!_.isEmpty(session)) {
        if (url.pathname === session.page.pathname ) {
          window.dataLayer = window.dataLayer || [];
          dataLayer.push(session);
          sessionStorage.removeItem('params');
        }
      }
    }
  });
})(jQuery, _, drupalSettings);;
