(function ($) {
  var contact = {
    containerCheckavailHtml: '.speak-with-an-agent [data-ajax="checkavail"]',
    /**
     *
     * @param {containerCheckavailHtml}
     */
    initialize: function (params) {
      params = params || {};
      params.containerCheckavailHtml = params.containerCheckavailHtml ? params.containerCheckavailHtml : this.containerCheckavailHtml;
      intializeContactInitialize(params.containerCheckavailHtml);
    }
  };

  function intializeContactInitialize(containerHtml) {

    if (!$(containerHtml).length) return;

    var interval = setInterval(function () {
      if (cogeco.session.isSet) {
        $.ajax({
          url: "/" + cogeco.language.getCurrentLanguage() + "/ajax/contact/checkavail",
          beforeSend: function (xhr) {
            xhr.setRequestHeader('Cache-Control', 'no-cache');
          }
        })
          .done(function (data) {
            $(containerHtml).append(data);
          
            $(".fancybox").fancybox({
              toolbar  : false,
              smallBtn : "auto",
              arrows   : false,
              idleTime : false,
              keyboard: true,
              infobar: false,
              hideScrollbar: true,
              autoFocus: false,
              backFocus: true,
              trapFocus: true,
              fullScreen: false,
              iframe : {
                preload : false
              },
              btnTpl: {
                smallBtn:
                  '<button type="button" data-fancybox-close class="fancybox-button fancybox-close-small close-button" aria-label="' + Drupal.t('Close modal') + '">' +
                  '<svg xmlns="http://www.w3.org/2000/svg" version="1" viewBox="0 0 24 24"><path d="M13 12l5-5-1-1-5 5-5-5-1 1 5 5-5 5 1 1 5-5 5 5 1-1z"/></svg>' +
                  "</button>"
              },
              afterLoad: function (instance) {
                instance.$refs.container.attr('aria-labelledby', 'modal-checkavail');
              },
              afterShow: function () {
                $('.close-button').focus();
              }
            });
          });
        clearInterval(interval);
      }
    }, 100);

  }

  cogeco.contact = contact;

  /**
   * Retailers
   */

  var retailers = {
    initialize: function (params) {
      params = params || {};

      $('.retailer-find-a-store select').change(function (e) {
        e.preventDefault();
        $('.retailer-find-a-store .section-store').removeClass('open');
        $('.retailer-find-a-store .section-store[data-city=' + $(this).val() + ']').addClass('open');
      });


      let openPanel = function (elem) {
        elem.toggleClass('open');
      };

      $(document).on('click', '.block-accordion', function (e) {
        if ($(window).width() < 768) {
          let posY = e.pageY - $(this).offset().top;
          let elem = $(this).find('.tile');

          let firstPanel,
            secondPanel,
            thirdPanel = '';

          switch (elem.length) {
            case 1:
              if (posY >= 0 && posY <= 70) {
                openPanel(elem);
              }
              break;

            case 2:
              firstPanel = elem.first();
              secondPanel = elem.last();

              if (posY >= 0 && posY <= 70) {
                openPanel(firstPanel);
              }
              else if (posY >= firstPanel.height() && (posY <= firstPanel.height() + 70)) {
                openPanel(secondPanel);
              }
              break;

            case 3:
              firstPanel = elem.first();
              secondPanel = elem.eq(1);
              thirdPanel = elem.last();

              if (posY >= 0 && posY <= 70) {
                openPanel(firstPanel);
              }
              else if (posY >= firstPanel.height() && (posY <= firstPanel.height() + 70)) {
                openPanel(secondPanel);
              }
              else if (posY >= firstPanel.height() + secondPanel.height() && (posY <= firstPanel.height() + secondPanel.height() + 70)) {
                openPanel(thirdPanel);
              }
              break;
          }

        }

      });

      $(document).on('mousemove', '.block-accordion', function (e) {
        if ($(window).width() < 768) {
          let posY = e.pageY - $(this).offset().top;
          let elem = $(this).find('.tile');

          let firstPanel,
            secondPanel,
            thirdPanel = '';

          switch (elem.length) {
            case 1:
              if (posY >= 0 && posY <= 70) {
                $(this).css('cursor', 'pointer');
              }
              else {
                $(this).css('cursor', 'default');
              }
              break;

            case 2:
              firstPanel = elem.first();
              secondPanel = elem.last();

              if (posY >= 0 && posY <= 70) {
                $(this).css('cursor', 'pointer');
              }
              else if (posY >= firstPanel.height() && (posY <= firstPanel.height() + 70)) {
                $(this).css('cursor', 'pointer');
              }
              else {
                $(this).css('cursor', 'default');
              }
              break;

            case 3:
              firstPanel = elem.first();
              secondPanel = elem.eq(1);
              thirdPanel = elem.last();

              if (posY >= 0 && posY <= 70) {
                $(this).css('cursor', 'pointer');
              }
              else if (posY >= firstPanel.height() && (posY <= firstPanel.height() + 70)) {
                $(this).css('cursor', 'pointer');
              }
              else if (posY >= firstPanel.height() + secondPanel.height() && (posY <= firstPanel.height() + secondPanel.height() + 70)) {
                $(this).css('cursor', 'pointer');
              }
              else {
                $(this).css('cursor', 'default');
              }
              break;
          }
        }
      });

    }
  };

  cogeco.retailers = retailers;
  cogeco.retailers.initialize();

})(jQuery);

