(function($, _, drupalSettings){
  $(function(){
    let three_boxes = drupalSettings.three_boxes || {};
    let market = drupalSettings.market ? drupalSettings.market : null;
    let currentLanguage = drupalSettings.path ? drupalSettings.path.currentLanguage : null;
    let callUs = $('#block-blockthreeboxes .blockcallus');
    let menuCallUs = $('#callUs');
    let phone = $('.phone-number');

    if (_.isEmpty(market)) {
      market = 'residential';
    }

    if (_.isEmpty(currentLanguage)) {
      currentLanguage = 'en';
    }

    $(document).ready(function() {
      let token = null;
      let pathName = window.location.pathname;

      /**
      * Lookin for what url match with token.
      */
      for (let key in three_boxes) {
        for (let id in three_boxes[key]) {
          if (pathName.toString() === three_boxes[key][id]) {
            token = key;
          }
        }
      }

      if (_.isEmpty(token)) {
        token = 'sales-phone';
      }

      if (!_.isEmpty(token)) {
        $.ajax({
          url: `/${currentLanguage}/${market}/getphone/${token}`,
          accepts: {},
          dataType: 'json',
        }).done(function(data) {
          if (data !== null) {
            callUs.text(formatPhoneNumber(data.phone)).attr('href', 'tel:+' + data.phone.replaceAll('-', '')).addClass('fontReducer');
            menuCallUs.text(formatPhoneNumber(data.phone)).attr('href', 'tel:+' + data.phone.replaceAll('-', '')).addClass('fontReducer');
            phone.text(formatPhoneNumber(data.phone)).attr('href', 'tel:+' + data.phone.replaceAll('-', '')).addClass('fontReducer');
          } else {
            console.log('No phone');
          }
        });
      }

      /**
       * Format phone number for French
       */
       function formatPhoneNumber(phoneNumber) {

        if (currentLanguage === 'fr') {
          phoneNumber = phoneNumber.replace(/(\d+)-(\d+)-(\d+)-(\d+)/, '$1 $2 $3-$4');
        }

        return phoneNumber;
      }
    })

  });
})(jQuery, _, drupalSettings)
